{%codeblock "simple currying" lang:javascript%}
(function () {
    //  
    function sum(x, y) {
        return x + y;
    }
    //  
    var makeAdder = function (x) {
        return function (y) {
            return sum(x, y);
        };
    };
    // sum  adderFour .
    var adderFour = makeAdder(4);
    console.log(adderFour(1)); // === 5
